import React from 'react';

// let bg = '/img/slider.jpg';
// let bgCss = 'no-repeat center center / cover';
// let bgStyle = {
//   background: 'url(' + bg + ')' + bgCss,
// };

let initData = {
  heading: 'Cannabis Sensory Experience',
  content: `En la provincia de Loja, ubicado a 1.700 metros sobre el nivel del mar, se encuentra el valle sagrado de <b>Vilcabamba<b/>, un lugar único en el mundo donde es común vivir más de 100 años. ¿El secreto? la calidad de vida, del agua, y de los alimentos que provienen de esta región.`,
  heroImage: '/img/faq-right.png',
  actionShapeImage: '/img/shape-bg.png',
};

let slider_action_data = [
  //   {
  //     link: 'apple',
  //     btnClass: 'btn softo-solid-btn',
  //     faClass: 'fa fa-apple',
  //     value: 'Appstore',
  //   },
  {
    link: 'android',
    btnClass: 'btn btn-icon',
    faClass: 'fa fa-android',
    value: '',
  },
  //   {
  //     link: 'windows',
  //     btnClass: 'btn btn-icon',
  //     faClass: 'fa fa-windows',
  //     value: '',
  //   },
];

class HeroSection extends React.Component {
  state = {
    initData: {},
    sliderData: [],
  };
  componentDidMount() {
    this.setState({
      initData: initData,
      sliderData: slider_action_data,
    });
  }

  render() {
    return (
      <div>
        {/* start slider background video section */}
        <div id="hero-section">
          <div className="hero-background-video">
            <div className="video-section-wrap-two">
              <div className="pattern-overlay">
                <div
                  className="player"
                  data-property="{videoURL:'https://www.youtube.com/watch?v=RTMoaqBqskc',containment:'.video-section-wrap-two', quality:'highres', autoPlay:true, showControls: false, startAt:0, stopAt: 43, loop:true, mute:true, opacity: 1}"
                ></div>
                <div className="container">
                  <div className="row">
                    <div className="col-md-8 col-md-offset-2">
                      <div className="hero-content text-center">
                        <h1>{this.state.initData.heading}</h1>
                        <p>{this.state.initData.content}</p>
                        <div className="slider-action-btn">
                          {this.state.sliderData.map((item, idx) => {
                            return (
                              <>
                                <img
                                  src={'/img/vilca-logo-hoja-naranaja.png'}
                                  alt="logo-marca"
                                  className="logo-hero"
                                />
                              </>
                              //   <a
                              //     key={`h_${idx}`}
                              //     href={item.link}
                              //     className="btn btn-icon"
                              //   >
                              //     <i className={item.faClass}></i>
                              //   </a>
                            );
                          })}
                          {/*                                             
                                            <a href="#" className="btn btn-icon"><i className="fa fa-android"></i></a>
                                            <a href="#" className="btn btn-icon"><i className="fa fa-windows"></i></a> */}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
        {/* end slider background video section */}
      </div>
    );
  }
}
export default HeroSection;
